import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";

const magic = new Magic("pk_live_845610B169B276D7");

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [file, setFile] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);

        // Check localStorage
        const savedData = localStorage.getItem(userMetadata.email);
        if (savedData) {
          const { name, imageUrl } = JSON.parse(savedData);
          setName(name);
          setImageUrl(imageUrl);
          setIsRegistered(true);
        }
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);

      // Check localStorage after login
      const savedData = localStorage.getItem(userMetadata.email);
      if (savedData) {
        const { name, imageUrl } = JSON.parse(savedData);
        setName(name);
        setImageUrl(imageUrl);
        setIsRegistered(true);
      }
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const uploadToBackend = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5001/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      const ipfsUrl = `https://gateway.pinata.cloud/ipfs/${data.ipfsHash}`;
      console.log("ðŸ“¦ Uploaded to:", ipfsUrl);
      return ipfsUrl;
    } catch (err) {
      console.error("âŒ Error uploading to backend:", err);
      return null;
    }
  };

  const registerIdentity = async (ipfsUrl) => {
    try {
      const res = await fetch("http://localhost:5001/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, ipfsHash: ipfsUrl.split("/").pop() }),
      });

      const data = await res.json();
      if (res.ok) {
        // Save to localStorage
        localStorage.setItem(user.email, JSON.stringify({ name, imageUrl: ipfsUrl }));
        setImageUrl(ipfsUrl);
        setIsRegistered(true);
        alert("âœ… Identity successfully registered!");
      } else {
        console.error("âŒ Backend error:", data.error);
        alert("âŒ Failed to register identity.");
      }
    } catch (err) {
      console.error("âŒ Error calling backend:", err);
    }
  };

  const handleRegister = async () => {
    if (!name || !file) {
      alert("Please fill in name and upload a file.");
      return;
    }

    const ipfsUrl = await uploadToBackend();
    if (!ipfsUrl) return;

    await registerIdentity(ipfsUrl);
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
    setName("");
    setFile(null);
    setImageUrl(null);
    setIsRegistered(false);
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>

        {isRegistered ? (
          <>
            <p><strong>Name:</strong> {name}</p>
            <p><strong>Uploaded Image:</strong></p>
            <img src={imageUrl} alt="Uploaded" width="200" />
            <br /><br />
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              style={{ padding: "8px", marginBottom: "8px" }}
            /><br />

            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              style={{ padding: "8px", marginBottom: "8px" }}
            /><br />

            <button onClick={handleRegister} style={{ marginRight: "8px" }}>
              Register Identity
            </button>
            <button onClick={handleLogout}>Logout</button>
          </>
        )}
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px" }}
      />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
