import { ethers } from 'ethers';
import MigrantID from './contracts/MigrantID.json';

const CONTRACT_ADDRESS = '0x37657A4D05AEc89d0bC1C17f126a8bB89D014a4b';

export const getContractInstance = async (provider) => {
  const signer = provider.getSigner();
  return new ethers.Contract(CONTRACT_ADDRESS, MigrantID.abi, signer);
};
