// MagicLogin.js
import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";

const magic = new Magic("pk_live_845610B169B276D7"); // replace with your real key

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");

  useEffect(() => {
    magic.user.isLoggedIn().then(async (loggedIn) => {
      if (loggedIn) {
        const metadata = await magic.user.getMetadata();
        setUser(metadata);
      }
    });
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const metadata = await magic.user.getMetadata();
      setUser(metadata);
    } catch (err) {
      console.error("Login error", err);
    }
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px" }}
      />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
