import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";
import { ethers } from "ethers";
import MIGRANT_ID_ABI from "./contracts/MigrantID.json";

const magic = new Magic("pk_live_845610B169B276D7"); // Use pk_test_... in dev
const CONTRACT_ADDRESS = "0x37657A4D05AEc89d0bC1C17f126a8bB89D014a4b"; // Replace this!

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [file, setFile] = useState(null);
  const [contract, setContract] = useState(null);

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
        await connectToContract();
      }
    })();
  }, []);

  const connectToContract = async () => {
    const provider = new ethers.providers.Web3Provider(magic.rpcProvider);
    const signer = provider.getSigner();
    const migrantContract = new ethers.Contract(CONTRACT_ADDRESS, MIGRANT_ID_ABI.abi, signer);
    setContract(migrantContract);

    const network = await provider.getNetwork();
    console.log("âœ… Connected to network:", network.name);
    console.log("âœ… Smart contract is connected and ready.");
  };

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
      await connectToContract();
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const uploadToPinata = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("https://api.pinata.cloud/pinning/pinFileToIPFS", {
        method: "POST",
        headers: {
          Authorization: `Bearer YOUR_PINATA_JWT` // Replace this!
        },
        body: formData
      });

      const data = await res.json();
      console.log("ðŸ“¦ IPFS Hash:", data.IpfsHash);
      return data.IpfsHash;
    } catch (err) {
      console.error("âŒ Error uploading to Pinata:", err);
      return null;
    }
  };

  const handleRegister = async () => {
    if (!contract || !name || !file) {
      alert("Please fill in name and upload a file.");
      return;
    }

    const ipfsHash = await uploadToPinata();
    if (!ipfsHash) return;

    try {
      const tx = await contract.createIdentity(name, ipfsHash);
      await tx.wait();
      alert("âœ… Identity successfully registered!");
    } catch (err) {
      console.error("âŒ Error writing to contract:", err);
    }
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>

        <input
          type="text"
          placeholder="Enter your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px" }}
        /><br />

        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          style={{ padding: "8px", marginBottom: "8px" }}
        /><br />

        <button onClick={handleRegister} style={{ marginRight: "8px" }}>Register Identity</button>
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px" }}
      />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );a
}

export default MagicLogin;
