import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";

const magic = new Magic("pk_live_845610B169B276D7"); // Use your public key

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [file, setFile] = useState(null);

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
    setName("");
    setFile(null);
  };

  const uploadToBackend = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5001/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      return data.ipfsHash;
    } catch (err) {
      console.error("Upload failed:", err);
      return null;
    }
  };

  const handleRegister = async () => {
    if (!name || !file) {
      alert("Please provide name and file.");
      return;
    }

    const ipfsHash = await uploadToBackend();
    if (!ipfsHash) {
      alert("Upload failed.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5001/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name, ipfsHash }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("✅ Identity registered on-chain!");
      } else {
        console.error("Registration failed:", data.error);
        alert("❌ Failed to register on-chain.");
      }
    } catch (err) {
      console.error("Register request failed:", err);
      alert("❌ Backend error during register.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      {user ? (
        <div>
          <p><strong>Logged in as:</strong> {user.email}</p>
          <p><strong>Wallet:</strong> {user.publicAddress}</p>

          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          /><br /><br />

          <input
            type="file"
            accept=".pdf,.jpg,.jpeg,.png"
            onChange={(e) => setFile(e.target.files[0])}
          /><br /><br />

          <button onClick={handleRegister}>📤 Register Identity</button>
          <button onClick={handleLogout} style={{ marginLeft: "10px" }}>Logout</button>
        </div>
      ) : (
        <div>
          <input
            type="email"
            placeholder="Enter email to login"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleLogin}>🔐 Login with Magic</button>
        </div>
      )}
    </div>
  );
}

export default MagicLogin;
