import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";
import { ethers } from "ethers";
import MIGRANT_ID_ABI from "./contracts/MigrantID.json"; // âœ… Your contract ABI here

const magic = new Magic("pk_live_845610B169B276D7");

const CONTRACT_ADDRESS = "0xYourContractAddressHere"; // âœ… Replace with your actual address

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
        await testContractConnection();
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
      await testContractConnection();
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const testContractConnection = async () => {
    try {
      const provider = new ethers.providers.Web3Provider(magic.rpcProvider);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(CONTRACT_ADDRESS, MIGRANT_ID_ABI.abi, signer);

      const network = await provider.getNetwork();
      console.log("âœ… Connected to network:", network.name);

      // ðŸ” Example: Call a contract method (update this to a real one)
      // const result = await contract.getSomething(); 
      // console.log("ðŸ“¦ Contract call result:", result);

      console.log("âœ… Smart contract is connected and ready.");
    } catch (error) {
      console.error("âŒ Error connecting to contract:", error);
    }
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px" }}
      />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
