// MagicLogin.js
import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";
import { ethers } from "ethers";

const magic = new Magic("YOUR_MAGIC_PUBLISHABLE_KEY", {
  network: {
    rpcUrl: "https://polygon-amoy.g.alchemy.com/v2/YOUR_ALCHEMY_API_KEY", // Amoy testnet
    chainId: 80002,
  },
});

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [address, setAddress] = useState(null);

  useEffect(() => {
    checkLoggedIn();
  }, []);

  const checkLoggedIn = async () => {
    const isLoggedIn = await magic.user.isLoggedIn();
    if (isLoggedIn) {
      const metadata = await magic.user.getMetadata();
      setUser(metadata);
      const provider = new ethers.providers.Web3Provider(magic.rpcProvider);
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
    }
  };

  const login = async () => {
    await magic.auth.loginWithSMS({ phoneNumber: "+91XXXXXXXXXX" }); // Replace or use input
    checkLoggedIn();
  };

  const logout = async () => {
    await magic.user.logout();
    setUser(null);
    setAddress(null);
  };

  return (
    <div>
      {user ? (
        <div>
          <p><strong>Phone:</strong> {user.phoneNumber}</p>
          <p><strong>Address:</strong> {address}</p>
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <button onClick={login}>Login with Phone</button>
      )}
    </div>
  );
}

export default MagicLogin;
