  'magic-sdk': 'magic-sdk' as const,
  '@magic-sdk/react-native': 'magic-sdk-rn' as const,
  '@magic-sdk/react-native-bare': 'magic-sdk-rn-bare' as const,
  '@magic-sdk/react-native-expo': 'magic-sdk-rn-expo' as const,
};
