/** */
export class RPCProviderModule extends BaseModule implements TypedEmitter {
  // Implements EIP 1193:
  // https://github.com/ethereum/EIPs/blob/master/EIPS/eip-1193.md

  public readonly isMagic = true;

  public sendAsync(payload: Partial<JsonRpcRequestPayload>, onRequestComplete: JsonRpcRequestCallback): void;
  public sendAsync(payload: Partial<JsonRpcRequestPayload>[], onRequestComplete: JsonRpcBatchRequestCallback): void;
  public sendAsync(
    payload: Partial<JsonRpcRequestPayload> | Partial<JsonRpcRequestPayload>[],
    onRequestComplete: JsonRpcRequestCallback | JsonRpcBatchRequestCallback,
  ): void;

  public sendAsync(
    payload: Partial<JsonRpcRequestPayload> | Partial<JsonRpcRequestPayload>[],
    onRequestComplete: JsonRpcRequestCallback | JsonRpcBatchRequestCallback,
  ): void {
    if (!onRequestComplete) {
      throw createInvalidArgumentError({
        procedure: 'Magic.rpcProvider.sendAsync',
        argument: 1,
        expected: 'function',
        received: onRequestComplete === null ? 'null' : typeof onRequestComplete,
      });
    }

    if (Array.isArray(payload)) {
      this.overlay
        .post(
          MagicOutgoingWindowMessage.MAGIC_HANDLE_REQUEST,
          payload.map(p => {
            const standardizedPayload = standardizeJsonRpcRequestPayload(p);
            this.prefixPayloadMethodForTestMode(standardizedPayload);
            return standardizedPayload;
          }),
        )
        .then(batchResponse => {
          (onRequestComplete as JsonRpcBatchRequestCallback)(
            null,
            batchResponse.map(response => ({
              ...response.payload,
              error: response.hasError ? new MagicRPCError(response.payload.error) : null,
            })),
          );
        });
    } else {
      const finalPayload = standardizeJsonRpcRequestPayload(payload);
      this.prefixPayloadMethodForTestMode(finalPayload);
      this.overlay.post(MagicOutgoingWindowMessage.MAGIC_HANDLE_REQUEST, finalPayload).then(response => {
        (onRequestComplete as JsonRpcRequestCallback)(
          response.hasError ? new MagicRPCError(response.payload.error) : null,
          response.payload,
        );
      });
    }
  }

  public send<ResultType = any>(method: string, params?: any[]): PromiEvent<ResultType>;
  public send(
    payload: JsonRpcRequestPayload | JsonRpcRequestPayload[],
    onRequestComplete: JsonRpcRequestCallback,
  ): void;
  public send<ResultType>(payload: JsonRpcRequestPayload, none: void): JsonRpcResponsePayload<ResultType>;

  public send<ResultType = any>(
    payloadOrMethod: string | JsonRpcRequestPayload | JsonRpcRequestPayload[],
    onRequestCompleteOrParams: JsonRpcRequestCallback | any[] | void,
  ): PromiEvent<ResultType> | JsonRpcResponsePayload<ResultType> | void {
    // Case #1
    // Web3 >= 1.0.0-beta.38 calls `send` with method and parameters.
    if (typeof payloadOrMethod === 'string') {
      const payload = createJsonRpcRequestPayload(
        payloadOrMethod,
        Array.isArray(onRequestCompleteOrParams) ? onRequestCompleteOrParams : [],
      );
      return this.request(payload) as any;
    }

    // Case #2
    // Web3 <= 1.0.0-beta.37 uses `send` with a callback for async queries.
    if (Array.isArray(payloadOrMethod) || !!onRequestCompleteOrParams) {
      this.sendAsync(payloadOrMethod, onRequestCompleteOrParams as any);
      return;
    }

    // Case #3
    // Legacy synchronous methods (unsupported).
    const warning = createSynchronousWeb3MethodWarning();
    warning.log();
    return new JsonRpcResponse(payloadOrMethod).applyError({
      code: -32603,
      message: warning.rawMessage,
    }).payload;
  }

  public enable() {
    const requestPayload = createJsonRpcRequestPayload(MagicPayloadMethod.Login);
    return this.request<string[], ProviderEnableEvents>(requestPayload);
  }

  /**
   * Here, we wrap `BaseModule.request` with an additional check
   * to determine if the RPC method requires a test-mode prefix.
   */
  protected request<ResultType = any, Events extends EventsDefinition = void>(payload: Partial<JsonRpcRequestPayload>) {
    this.prefixPayloadMethodForTestMode(payload);
    return super.request<ResultType, Events>(payload);
  }

  /**
   * Prefixes Ethereum RPC methods with a `testMode` identifier. This is done so
   * that Magic's <iframe> can handle signing methods using test-specific keys.
   */
  private prefixPayloadMethodForTestMode(payload: Partial<JsonRpcRequestPayload>) {
    const testModePrefix = 'testMode/eth/';

    // In test mode, we prefix all RPC methods with `test/` so that the
    // Magic <iframe> can handle them without requiring network calls.
    if (this.sdk.testMode) {
      payload.method = `${testModePrefix}${payload.method}`;
    }
  }

  public on = createChainingEmitterMethod('on', this);
  public once = createChainingEmitterMethod('once', this);
  public addListener = createChainingEmitterMethod('addListener', this);

  public off = createChainingEmitterMethod('off', this);
  public removeListener = createChainingEmitterMethod('removeListener', this);
  public removeAllListeners = createChainingEmitterMethod('removeAllListeners', this);

  public emit = createBoundEmitterMethod('emit');
  public eventNames = createBoundEmitterMethod('eventNames');
  public listeners = createBoundEmitterMethod('listeners');
  public listenerCount = createBoundEmitterMethod('listenerCount');
}
import { createJsonRpcRequestPayload, standardizeJsonRpcRequestPayload } from '../core/json-rpc';
import { BaseModule } from './base-module';
import { SDKBase, MagicSDKAdditionalConfiguration, MagicSDKExtensionsOption } from '../core/sdk';
import { createExtensionNotInitializedError, MagicExtensionError, MagicExtensionWarning } from '../core/sdk-exceptions';
import { createPromiEvent, encodeJSON, decodeJSON, storage, isPromiEvent } from '../util';

const sdkAccessFields = ['request', 'overlay', 'sdk'];

/**
 * From the `BaseExtension`-derived instance, get the prototype
 * chain up to and including the `BaseModule` class.
 */
function getPrototypeChain<T extends BaseExtension<string>>(instance: T) {
  let currentProto = Object.getPrototypeOf(instance);
  const protos = [currentProto];

  while (currentProto !== BaseModule.prototype) {
    currentProto = Object.getPrototypeOf(currentProto);
    protos.push(currentProto);
  }

  return protos;
}

export abstract class BaseExtension<TName extends string> extends BaseModule {
  /**
   * A structure describing the platform and version compatiblity of this
   * extension.
   */
  compat?: {
    'magic-sdk': boolean | string;
    '@magic-sdk/react-native': boolean | string;
    '@magic-sdk/react-native-bare': boolean | string;
    '@magic-sdk/react-native-expo': boolean | string;
  };

  public abstract readonly name: TName;

  private __sdk_access_field_descriptors__ = new Map<
    string,
    { descriptor: PropertyDescriptor; isPrototypeField: boolean }
  >();
  private __is_initialized__ = false;

  protected utils = {
    createPromiEvent,
    isPromiEvent,
    encodeJSON,
    decodeJSON,
    createJsonRpcRequestPayload,
    standardizeJsonRpcRequestPayload,
    storage,
  };

  constructor() {
    super(undefined as any);

    // Disallow SDK access before initialization...

    const allSources = [this, ...getPrototypeChain(this)];

    sdkAccessFields.forEach(prop => {
      const allDescriptors = allSources.map(source => Object.getOwnPropertyDescriptor(source, prop));
      const sourceIndex = allDescriptors.findIndex(x => Boolean(x));
      const isPrototypeField = sourceIndex > 0;
      const descriptor = allDescriptors[sourceIndex];

      /* istanbul ignore else */
      if (descriptor) {
        this.__sdk_access_field_descriptors__.set(prop, { descriptor, isPrototypeField });

        Object.defineProperty(this, prop, {
          configurable: true,
          get: () => {
            throw createExtensionNotInitializedError(prop);
          },
        });
      }
    });
  }

  /**
   * Registers a Magic SDK instance with this Extension.
   *
   * @internal
   */
  public init(sdk: SDKBase) {
    if (this.__is_initialized__) return;

    // Replace original property descriptors
    // for SDK access fields post-initialization.
    sdkAccessFields.forEach(prop => {
      /* istanbul ignore else */
      if (this.__sdk_access_field_descriptors__.has(prop)) {
        const { descriptor, isPrototypeField } = this.__sdk_access_field_descriptors__.get(prop)!;

        if (isPrototypeField) {
          // For prototype fields, we just need the `delete` operator so that
          // the instance will fallback to the prototype chain itself.
          delete this[prop as keyof this];
        } else {
          Object.defineProperty(this, prop, descriptor);
        }
      }
    });

    this.sdk = sdk;
    this.__is_initialized__ = true;
  }

  /**
   * Creates a deprecation warning wrapped with a native Magic SDK warning type.
   * Best practice is to warn users of upcoming deprecations at least one major
   * version before the change is implemented. You can use this method to
   * communicate deprecations in a manner consistent with Magic SDK core code.
   */
  protected createDeprecationWarning(options: {
    method: string;
    removalVersion: string;
    useInstead?: string;
  }): MagicExtensionWarning {
    const { method, removalVersion, useInstead } = options;

    const useInsteadSuffix = useInstead ? ` Use \`${useInstead}\` instead.` : '';
    const message = `\`${method}\` will be removed from this Extension in version \`${removalVersion}\`.${useInsteadSuffix}`;
    return new MagicExtensionWarning(this, 'DEPRECATION_NOTICE', message);
  }

  /**
   * Creates a warning wrapped with a native Magic SDK warning type. This
   * maintains consistency in warning messaging for consumers of Magic SDK and
   * this Extension.
   */
  protected createWarning(code: string | number, message: string): MagicExtensionWarning {
    return new MagicExtensionWarning(this, code, message);
  }

  /**
   * Creates an error wrapped with a native Magic SDK error type. This maintains
   * consistency in error handling for consumers of Magic SDK and this
   * Extension.
   */
  protected createError<TData = any>(code: string | number, message: string, data: TData): MagicExtensionError<TData> {
    return new MagicExtensionError<TData>(this, code, message, data);
  }
}

abstract class InternalExtension<TName extends string, TConfig extends any = any> extends BaseExtension<TName> {
  public abstract readonly config: TConfig;
}

/**
 * A base class representing "extensions" to the core Magic JS APIs. Extensions
 * enable new functionality by composing Magic endpoints methods together.
 */
export class Extension {
  /**
   * This is a special constructor used to mark "official" extensions. These
   * extensions are designed for special interaction with the Magic iframe using
   * custom JSON RPC methods, business logic, and global configurations. This is
   * intended for internal-use only (and provides no useful advantage to
   * open-source extension developers over the regular `Extension` class).
   *
   * @internal
   */
  public static Internal = InternalExtension;
}

/**
 * These fields are exposed on the `Extension` type,
 * but should be hidden from the public interface.
 */
type HiddenExtensionFields = 'name' | 'init' | 'config' | 'compat';

/**
 * Gets the type contained in an array type.
 */
type UnwrapArray<T extends any[]> = T extends Array<infer P> ? P : never;

/**
 * Create a union type of Extension names from an
 * array of Extension types given by `TExt`.
 */
type ExtensionNames<TExt extends BaseExtension<string>[]> =
  UnwrapArray<TExt> extends BaseExtension<infer R> ? R : never;

/**
 * From the literal Extension name type given by `TExtName`,
 * extract a dictionary of Extension types.
 */
type GetExtensionFromName<TExt extends BaseExtension<string>[], TExtName extends string> = {
  [P in TExtName]: Extract<UnwrapArray<TExt>, BaseExtension<TExtName>>;
}[TExtName];

/**
 * Wraps a Magic SDK constructor with the necessary type
 * information to support a strongly-typed `Extension` interface.
 */
export type WithExtensions<SDK extends SDKBase> = {
  new <TCustomExtName extends string, TExt extends MagicSDKExtensionsOption<TCustomExtName>>(
    apiKey: string,
    options?: MagicSDKAdditionalConfiguration<TCustomExtName, TExt>,
  ): InstanceWithExtensions<SDK, TExt>;
};

export type InstanceWithExtensions<SDK extends SDKBase, TExt extends MagicSDKExtensionsOption> = SDK & {
  [P in Exclude<
    TExt extends BaseExtension<string>[] ? ExtensionNames<TExt> : keyof TExt,
    number
  >]: TExt extends BaseExtension<string>[]
    ? Omit<GetExtensionFromName<TExt, P>, HiddenExtensionFields>
    : TExt extends {
          [P in Exclude<
            TExt extends BaseExtension<string>[] ? ExtensionNames<TExt> : keyof TExt,
            number
          >]: BaseExtension<string>;
        }
      ? Omit<TExt[P], HiddenExtensionFields>
      : never;
};
import {
  MagicPayloadMethod,
  NFTCheckoutRequest,
  NFTCheckoutResponse,
  NFTCheckoutEvents,
  NFTPurchaseRequest,
  NFTPurchaseResponse,
  NFTTransferRequest,
  NFTTransferResponse,
  NftCheckoutIntermediaryEvents,
} from '@magic-sdk/types';
import { BaseModule } from './base-module';
import { createJsonRpcRequestPayload } from '../core/json-rpc';

export class NFTModule extends BaseModule {
  /* Start an NFT Purchase flow with Sardine */
  public purchase(options: NFTPurchaseRequest) {
    const requestPayload = createJsonRpcRequestPayload(MagicPayloadMethod.NFTPurchase, [options]);
    return this.request<NFTPurchaseResponse>(requestPayload);
  }

  /* Start an NFT Checkout flow with Paypal */
  public checkout(options: NFTCheckoutRequest) {
    const isThirdPartyWalletConnected = this.sdk.thirdPartyWallets.isConnected;

    const requestPayload = createJsonRpcRequestPayload(MagicPayloadMethod.NFTCheckout, [
      {
        ...options,
        walletProvider: isThirdPartyWalletConnected ? 'web3modal' : 'magic',
      },
    ]);
    const promiEvent = this.request<NFTCheckoutResponse, NFTCheckoutEvents>(requestPayload);

    if (isThirdPartyWalletConnected) {
      promiEvent.on(NftCheckoutIntermediaryEvents.Initiated, async (rawTransaction) => {
        try {
          const hash = await this.request({
            method: 'eth_sendTransaction',
            params: [rawTransaction],
          });
          this.createIntermediaryEvent(NftCheckoutIntermediaryEvents.Success, requestPayload.id as string)(hash);
        } catch (error) {
          this.createIntermediaryEvent(NftCheckoutIntermediaryEvents.Failure, requestPayload.id as string)();
        }
      });
      promiEvent.on(NftCheckoutIntermediaryEvents.Disconnect, () => {
        this.sdk.thirdPartyWallets.resetThirdPartyWalletState();
        promiEvent.emit('disconnect');
      });
    }
    return promiEvent;
  }

  /* Start an NFT Transfer flow */
  public transfer(options: NFTTransferRequest) {
    const requestPayload = createJsonRpcRequestPayload(MagicPayloadMethod.NFTTransfer, [options]);
    return this.request<NFTTransferResponse>(requestPayload);
  }
}
