import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";

const magic = new Magic("pk_live_845610B169B276D7"); // Replace with your key

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [form, setForm] = useState({
    name: "",
    age: "",
    gender: "",
    dob: "",
    file: null,
  });

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const uploadToBackend = async () => {
    const formData = new FormData();
    formData.append("file", form.file);

    try {
      const res = await fetch("http://localhost:5001/upload", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      return data.ipfsHash;
    } catch (err) {
      console.error("❌ Error uploading to backend:", err);
      return null;
    }
  };

  const registerIdentity = async (ipfsHash) => {
    const payload = {
      name: form.name,
      age: form.age,
      gender: form.gender,
      dob: form.dob,
      ipfsHash,
      email: user.email,
    };

    try {
      const res = await fetch("http://localhost:5001/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();
      if (res.ok) {
        alert("✅ Registered successfully!");
      } else {
        console.error("❌ Backend error:", data.error);
        alert("❌ Registration failed.");
      }
    } catch (err) {
      console.error("❌ Error:", err);
    }
  };

  const handleRegister = async () => {
    if (!form.name || !form.file) {
      alert("Please fill in all fields.");
      return;
    }

    const ipfsHash = await uploadToBackend();
    if (!ipfsHash) return;

    await registerIdentity(ipfsHash);
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>

        <input name="name" placeholder="Name" onChange={handleChange} /><br />
        <input name="age" placeholder="Age" onChange={handleChange} /><br />
        <input name="gender" placeholder="Gender" onChange={handleChange} /><br />
        <input name="dob" placeholder="Date of Birth" type="date" onChange={handleChange} /><br />
        <input name="file" type="file" onChange={handleChange} /><br />

        <button onClick={handleRegister}>Register Identity</button>
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  return (
    <div>
      <input type="email" placeholder="Enter your email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
