import { Magic } from 'magic-sdk';
import Web3 from 'web3';
import MigrantID from './build/contracts/MigrantID.json'; // adjust if needed

// 1. Magic SDK init with Alchemy RPC (Polygon Amoy testnet)
const magic = new Magic('pk_live_845610B169B276D7', {
  network: {
    rpcUrl: 'https://polygon-amoy.g.alchemy.com/v2/YOUR_ALCHEMY_KEY',
    chainId: 80002,
  },
});

// 2. Create web3 instance from Magic's provider
const web3 = new Web3(magic.rpcProvider);

// 3. Connect to deployed contract
const CONTRACT_ADDRESS = '0x37657A4D05AEc89d0bC1C17f126a8bB89D014a4b'; // Replace with real one

const migrantContract = new web3.eth.Contract(
  MigrantID.abi,
  CONTRACT_ADDRESS
);

// 4. Export for use
export { web3, migrantContract, magic };
