import { Contract } from "ethers";
import abi from "./MigrantABI.json"; // Must exist in the same folder
const contractAddress = "0x37657A4D05AEc89d0bC1C17f126a8bB89D014a4b"; // Replace with real one

export const getContractInstance = (signer) => {
  return new Contract(contractAddress, abi, signer);
};
