import React, { useState } from "react";
import { Magic } from "magic-sdk";
import { ethers } from "ethers";
import { getContractInstance } from "./contractUtils"; // ABI helper

const magic = new Magic("pk_live_845610B169B276D7"); // Replace with your Magic publishable key

const MagicLogin = () => {
  const [user, setUser] = useState(null);

  const login = async () => {
    try {
      const email = prompt("Enter your email:");
      await magic.auth.loginWithEmailOTP({ email });

      const provider = new ethers.providers.Web3Provider(magic.rpcProvider);
      const signer = provider.getSigner();
      const address = await signer.getAddress();

      setUser(address);
      console.log("✅ Logged in as:", address);

      // Interact with smart contract
      const contract = getContractInstance(signer);
      const tx = await contract.registerMigrant("Alice", 28);
      await tx.wait();

      console.log("✅ Migrant registered successfully!");
    } catch (err) {
      console.error("❌ Login or contract call failed:", err);
    }
  };

  const logout = async () => {
    await magic.user.logout();
    setUser(null);
    console.log("👋 Logged out");
  };

  return (
    <div>
      {user ? (
        <>
          <p>✅ Logged in as: {user}</p>
          <button onClick={logout}>Logout</button>
        </>
      ) : (
        <button onClick={login}>Login with Email</button>
      )}
    </div>
  );
};

export default MagicLogin;
