import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";

const magic = new Magic("pk_live_845610B169B276D7"); // Replace with test key in dev

function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [file, setFile] = useState(null);
  const [ipfsData, setIpfsData] = useState(null);

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  const uploadToBackend = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5001/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      console.log("ðŸ“¦ IPFS Hash from backend:", data.ipfsHash);
      return data.ipfsHash;
    } catch (err) {
      console.error("âŒ Error uploading to backend:", err);
      return null;
    }
  };

  const registerIdentity = async (ipfsHash) => {
    try {
      const res = await fetch("http://localhost:5001/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, ipfsHash }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("âœ… Identity successfully registered!");

        // ðŸ‘‰ Fetch metadata JSON from IPFS
        const ipfsURL = `https://gateway.pinata.cloud/ipfs/${ipfsHash}`;
        const ipfsRes = await fetch(ipfsURL);
        const ipfsJson = await ipfsRes.json();
        setIpfsData(ipfsJson);
        console.log("ðŸ“„ IPFS Metadata:", ipfsJson);

      } else {
        console.error("âŒ Backend error:", data.error);
        alert("âŒ Failed to register identity.");
      }
    } catch (err) {
      console.error("âŒ Error calling backend:", err);
    }
  };

  const handleRegister = async () => {
    if (!name || !file) {
      alert("Please fill in name and upload a file.");
      return;
    }

    const ipfsHash = await uploadToBackend();
    if (!ipfsHash) return;

    await registerIdentity(ipfsHash);
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
    setName("");
    setFile(null);
    setIpfsData(null);
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>

        <input
          type="text"
          placeholder="Enter your name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px" }}
        /><br />

        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          style={{ padding: "8px", marginBottom: "8px" }}
        /><br />

        <button onClick={handleRegister} style={{ marginRight: "8px" }}>
          Register Identity
        </button>
        <button onClick={handleLogout}>Logout</button>

        {ipfsData && (
          <div style={{ marginTop: "20px" }}>
            <h3>ðŸ“œ Decentralized Profile</h3>
            <p><strong>Name:</strong> {ipfsData.name}</p>
            <p><strong>Age:</strong> {ipfsData.age}</p>
            <p><strong>Gender:</strong> {ipfsData.gender}</p>
            <p><strong>DOB:</strong> {ipfsData.dob}</p>
            <img
              src={`https://gateway.pinata.cloud/ipfs/${ipfsData.image}`}
              alt="Aadhaar"
              width="200"
              style={{ marginTop: "10px", border: "1px solid #ccc", padding: "5px" }}
            />
          </div>
        )}
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px" }}
      />
      <button onClick={handleLogin}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
