// magiclogin.js
import React, { useEffect, useState } from "react";
import { Magic } from "magic-sdk";
import { ethers } from "ethers"; // Import ethers for provider/signer

// IMPORTANT: Replace with your actual Magic Test Key for development
// For production, ensure pk_live_845610B169B276D7 is the correct production key.
// For Amoy testnet, ensure you've configured Magic accordingly if not using direct RPC.
const magic = new Magic("pk_live_845610B169B276D7", {
  network: {
    rpcUrl: process.env.REACT_APP_ALCHEMY_AMOY_URL, // Use env variable for Alchemy URL
    chainId: 80002, // Amoy chain ID
  },
});

// IMPORTANT: Replace with your deployed smart contract address
const CONTRACT_ADDRESS = "YOUR_DEPLOYED_CONTRACT_ADDRESS_HERE";
// IMPORTANT: Replace with your contract ABI
// You'll need to import your contract's ABI JSON artifact generated by Hardhat
// Example: import IdentityABI from './artifacts/contracts/Identity.sol/Identity.json';
// For now, I'll just put a placeholder for structure.
const CONTRACT_ABI = [
  // Example ABI entries - REPLACE WITH YOUR ACTUAL ABI
  "function registerIdentity(string _name, uint256 _age, string _gender, string _dob, string _aadharHash, string _photoHash) public",
  "function getIdentity(address userAddress) public view returns (string, uint256, string, string, string, string)",
  // Add other functions if needed
];


function MagicLogin() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");

  // New state variables for user details
  const [fullName, setFullName] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [dob, setDob] = useState(""); // Date of Birth

  // File states
  const [aadharFile, setAadharFile] = useState(null);
  const [photoFile, setPhotoFile] = useState(null);

  useEffect(() => {
    (async () => {
      const isLoggedIn = await magic.user.isLoggedIn();
      if (isLoggedIn) {
        const userMetadata = await magic.user.getInfo();
        setUser(userMetadata);
      }
    })();
  }, []);

  const handleLogin = async () => {
    try {
      await magic.auth.loginWithEmailOTP({ email });
      const userMetadata = await magic.user.getInfo();
      setUser(userMetadata);
    } catch (err) {
      console.error("Login error:", err);
    }
  };

  // Generic upload function for Aadhar or Photo
  const uploadFileToBackend = async (fileToUpload, fileType) => {
    const formData = new FormData();
    formData.append("file", fileToUpload);
    formData.append("fileType", fileType); // Add a type identifier

    try {
      const res = await fetch("http://localhost:5001/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      if (res.ok) {
        console.log(`ðŸ“¦ ${fileType} IPFS Hash from backend:`, data.ipfsHash);
        return data.ipfsHash;
      } else {
        console.error(`âŒ Backend error uploading ${fileType}:`, data.error);
        alert(`âŒ Failed to upload ${fileType}.`);
        return null;
      }
    } catch (err) {
      console.error(`âŒ Error uploading ${fileType} to backend:`, err);
      return null;
    }
  };

  const registerIdentityOnContract = async (data) => {
    try {
        // Get the provider from Magic
        const provider = new ethers.BrowserProvider(magic.rpcProvider);
        // Get the signer (user's Magic wallet)
        const signer = await provider.getSigner();
        // Create a contract instance with the signer
        const identityContract = new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer);

        // Call the smart contract function
        // Note: For actual gasless transactions via a relayer, this part
        // might look different, potentially involving an EIP-2771 trusted forwarder setup
        // or a custom backend that signs and sends.
        // For simplicity here, we're assuming the Magic wallet directly sends the tx,
        // or your server's /register endpoint will handle true gasless.
        // Given your setup, your `server.js`'s `/register` endpoint *should*
        // be the one interacting with the contract using *your* private key for gas.
        // So, this frontend part would primarily send the data to the backend.

        // If your server is handling gas, you don't call contract directly here.
        // Instead, send all data to the backend for the server to handle the contract call.
        const res = await fetch("http://localhost:5001/register", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(data),
        });

        const result = await res.json();
        if (res.ok) {
            alert("âœ… Identity successfully registered on-chain!");
            console.log("Transaction Hash:", result.transactionHash);
        } else {
            console.error("âŒ Backend error during contract registration:", result.error);
            alert("âŒ Failed to register identity on-chain.");
        }

    } catch (err) {
        console.error("âŒ Error registering identity on contract via backend:", err);
        alert("âŒ Error registering identity on contract.");
    }
  };


  const handleRegister = async () => {
    if (!fullName || !age || !gender || !dob || !aadharFile || !photoFile) {
      alert("Please fill in all details and upload both Aadhar and Photo.");
      return;
    }

    // Upload Aadhar
    const aadharIpfsHash = await uploadFileToBackend(aadharFile, "aadhar");
    if (!aadharIpfsHash) return;

    // Upload Photo
    const photoIpfsHash = await uploadFileToBackend(photoFile, "photo");
    if (!photoIpfsHash) return;

    // Prepare data to send to backend for contract interaction
    const identityData = {
      name: fullName,
      age: parseInt(age), // Ensure age is a number
      gender,
      dob,
      aadharHash: aadharIpfsHash,
      photoHash: photoIpfsHash,
      userAddress: user.publicAddress // Pass the user's Magic wallet address
    };

    await registerIdentityOnContract(identityData);
  };

  const handleLogout = async () => {
    await magic.user.logout();
    setUser(null);
    setEmail("");
    setFullName("");
    setAge("");
    setGender("");
    setDob("");
    setAadharFile(null);
    setPhotoFile(null);
  };

  if (user) {
    return (
      <div>
        <p><strong>Logged in as:</strong> {user.email}</p>
        <p><strong>Public Address:</strong> {user.publicAddress}</p>

        <h2>Register Your Identity</h2>
        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />
        <input
          type="number"
          placeholder="Age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />
        <input
          type="text"
          placeholder="Gender"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />
        <input
          type="date"
          placeholder="Date of Birth"
          value={dob}
          onChange={(e) => setDob(e.target.value)}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />

        <label htmlFor="aadhar-upload">Upload Aadhar (File):</label>
        <input
          type="file"
          id="aadhar-upload"
          onChange={(e) => setAadharFile(e.target.files[0])}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />

        <label htmlFor="photo-upload">Upload Photo (File):</label>
        <input
          type="file"
          id="photo-upload"
          onChange={(e) => setPhotoFile(e.target.files[0])}
          style={{ padding: "8px", marginBottom: "8px", width: "100%", boxSizing: "border-box" }}
        /><br />

        <button onClick={handleRegister} style={{ marginRight: "8px", padding: "10px 15px" }}>
          Register Identity
        </button>
        <button onClick={handleLogout} style={{ padding: "10px 15px" }}>Logout</button>
      </div>
    );
  }

  return (
    <div>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: "8px", marginRight: "8px", width: "calc(100% - 120px)" }}
      />
      <button onClick={handleLogin} style={{ padding: "8px 15px" }}>Login with Magic Link</button>
    </div>
  );
}

export default MagicLogin;
